mapper.select:
SELECT id, stu_no as stuNo, stu_name as stuName, stu_class as stuClass
FROM stu
WHERE id = #{id}

mapper.update:
UPDATE stu
stu_no= #{stu.stuNo}, stu_name= #{stu.stuName}, stu_class= #{stu.stuClass},
WHERE id=#{id}

mapper.select:
SELECT s.id, s.stu_name, s.stu_no, sc.class_id, sc.class_name, sc.department
FROM stu s
LEFT JOIN stuclass sc ON s.stu_class = sc.class_id

     AND s.stu_name LIKE CONCAT('%', #{stu_name}, '%') 
     AND s.stu_no = #{stu_no} 
     AND s.stu_class = #{class_id}
